import React from 'react';
import FormInput from '../form-input/form-input.component';
import CustomButton from '../custom-button/custom-button.component';
import { auth,CreateUserProfile } from '../../firebase/firebase.utils';

class SignUp extends React.Component {

    constructor() {
        super();

        this.state = {
            displayName: '',
            email: '',
            password: '',
            confirmPassword: ''
        }

    }

    handleChange = (event) => {
        const { name, value } = event.target;

        this.setState({ [name]: value });
    }

    handleSubmit = async (event) => {
        event.preventDefault();
        console.log("Handle Submit");
        const { displayName, email, password, confirmPassword } = this.state;

        if (password !== confirmPassword) {
          alert("passwords don't match");
          return;
        }

        try {
          const { user } = await auth.createUserWithEmailAndPassword(
            email,
            password
          );

          await CreateUserProfile(user, { displayName });

          this.setState({
            displayName: '',
            email: '',
            password: '',
            confirmPassword: ''
          });
        } catch (error) {
          console.error(error);
        }
      };

    render() {
        const { displayName, email, password, confirmPassword } = this.state;
        return (
            <div className='sign-up'>
                <h2 className='title'>I do not have an account</h2>
                <span>Sign up with your email and password</span>
                <FormInput type="text" name="displayName" label="DisplayName" value={displayName} onChange={this.handleChange} onSubmit={this.handleSubmit} required />
                <FormInput type="email" name="email" label="Email" value={email} onChange={this.handleChange} onSubmit={this.handleSubmit} required />
                <FormInput type="password" name="password" label="Password" value={password} onChange={this.handleChange} onSubmit={this.handleSubmit} required />
                <FormInput type="password" name="confirmPassword" label="ConfirmPassword" value={confirmPassword} onChange={this.handleChange} onSubmit={this.handleSubmit} required />
                <CustomButton type="submit">SIGN UP</CustomButton>

            </div>
        )
    }

}


export default SignUp;
