import { createSelector} from 'reselect';
import memoize from 'lodash.memoize';

const selectShop=state=>state.shop;

const selectShopCollections=createSelector(
    [selectShop],
    shop=>shop.collections
)

export const selectShopCollection=memoize(collectionParam=> 
createSelector(
    [selectShopCollections],
    collections=>collections.find(collection=>collection.route===collectionParam)
))

export default selectShopCollections;
