import React from 'react';
import FormInput from '../form-input/form-input.component';
import CustomButton from '../custom-button/custom-button.component';
import { auth } from '../../firebase/firebase.utils';

class SignUp extends React.Component {

    constructor() {
        super();

        this.state = {
            displayName: '',
            email: '',
            password: '',
            confirmPassword: ''
        }

    }

    handleChange = (event) => {
        const { name, value } = event.target;

        this.setState({ [name]: value });
    }

    handleSubmit = (event) => {
        event.preventDefault();

        const { email, password } = this.state;

        x = auth.createUserWithEmailAndPassword(email, password);

    }

    render() {
        return (
            <div className='sign-up'>
                <h2 className='title'>I done have an account</h2>
                <span>Sign up with your email and password</span>
                <FormInput type="text" name="displayName" label="DisplayName" onChange={this.handleChange} onSubmit={this.handleSubmit} required />
                <FormInput type="email" name="email" label="Email" onChange={this.handleChange} onSubmit={this.handleSubmit} required />
                <FormInput type="password" name="password" label="Password" onChange={this.handleChange} onSubmit={this.handleSubmit} required />
                <FormInput type="password" name="confirmPassword" label="ConfirmPassword" onChange={this.handleChange} onSubmit={this.handleSubmit} required />
                <CustomButton type="submit">SIGN UP</CustomButton>

            </div>
        )
    }

}


export default SignUpl
