import { CartTypes } from '../../redux/cart/cart.types';

const INITIATL_STATE={
    hidden:true
}

const CartReducer= (state=INITIATL_STATE,action) => {
    switch (action.type){
        case CartTypes.TOGGLE_CART_VIEW:
            return {
                ...state,
                hidden: !state.hidden
            }
        default:
            return state;    
    }     
}

export default CartReducer;
