export const addItemsToCart = (cartItems, itemToAdd) => {

    const existingItem = cartItems.find(cartItem => cartItem.id === itemToAdd.id);

    if (existingItem) {
        return cartItems.map(cartItem =>
            cartItem.id === itemToAdd.id ? { ...cartItem, quantity: cartItem.quantity + 1 } : cartItem
        )
    }

    return [...cartItems, { ...itemToAdd, quantity: 1 }]

}


export const removeItemFromCart = (cartItems, itemToRemove) => {

    const existingItem = cartItems.find(cartItem => cartItem.id === itemToRemove.id);

    if (existingItem.quantity === 1) {
        console.log("cartItems"); 
        const newCart=cartItems.find(cartItem => cartItem.id !== itemToRemove.id);
        console.log(newCart);
        return [newCart];
    }
    return(cartItems.map(cartItem =>
        cartItem.id === itemToRemove.id ? {...cartItem,quantity:cartItem.quantity-1 } : cartItem));

}
